<?phpinclude_once ('models/M_Model.php');class C_Edit extends C_Base{	private $text;		private $error;	    protected $article_id;    protected $article_title ;    protected $article_content;	protected function OnInput()	{		parent::OnInput();        $m_model = M_Model::Instance();		$this->title = $this->title . ' :: Редактування';				if (isset($_POST['edit']))		{            if($m_model->articles_edit($_POST['id'], $_POST['title'], $_POST['article']))            {                header('Location: index.php?c=editor');                die();            }            $this->article_id = $_POST['id'];            $this->article_title = $_POST['title'];            $this->article_content = $_POST['article'];		}        elseif (isset($_POST['delete'])){            if($m_model->articles_delete($_POST['id']))            {                header('Location: index.php?c=editor');                die();            }        }		else		{		    $this->article_id=$_GET['id'];		    $this->article_title=$_GET['title'];            $this->article_content=$_GET['article'];		}	}			protected function OnOutput()	{		$vars = array('article_title' => $this->article_title,                      'article_content' => $this->article_content,                      'article_id' => $this->article_id,                      'error' => $this->error);		$this->content = $this->Template('views/v_edit.php', $vars);		parent::OnOutput();	}	}